        B, C, H, W = x.shape
        
        x = torch.nn.functional.adaptive_avg_pool2d(x, (4, 4))
        B, C, H, W = x.shape
        
        x = x.view(B, C, -1).transpose(1, 2)
        
        intermediate_features = torch.randn(B, H*W, 128, device=x.device)
        x = self.vit_output(intermediate_features, x)
        
