const { test, expect } = require('@playwright/test');

test('website screenshot test', async ({ page }) => {
  // Navigate to the website
  await page.goto('https://pingera.ru');

  // Wait for the page to fully load
  await page.waitForLoadState('load');

  // Additional wait to ensure all dynamic content is loaded
  await page.waitForTimeout(2000);

  // Take a full page screenshot
  await page.screenshot({ 
    path: 'website-screenshot.png', 
    fullPage: true 
  });

  // Verify the page loaded successfully
  await expect(page).toHaveTitle(/Pingera - платформа мониторинга/);

  // Check for key elements on the page
  await expect(page.locator('h1')).toBeVisible();

  console.log('Screenshot captured and page verified successfully');
});
