#import pytest

from pyparticle.species.base import AerosolSpecies


def test_aerosol_species_loads_defaults():
    s = AerosolSpecies('SO4')
    assert s.density is not None
    assert s.kappa is not None
    assert s.molar_mass is not None


def test_aerosol_species_unknown_raises():
    with pytest.raises(ValueError):
        AerosolSpecies('THIS_SPEC_DOES_NOT_EXIST')
