    Clu_blood_metc13 = x[32] / Vlu_blood      # [79] Clu_blood_metc13
    Clu_co2c13 = x[33] / Vlu_tissue      # [80] Clu_co2c13
    Clu_metc13 = x[34] / Vlu_tissue      # [81] Clu_metc13
    Cpl_ve_apap = Cve_apap / p[3]      # [82] Cpl_ve_apap
    Cpl_ve_co2c13 = Cve_co2c13 / p[4]      # [83] Cpl_ve_co2c13
    Cpl_ve_metc13 = Cve_metc13 / p[5]      # [84] Cpl_ve_metc13
    Csp_apap = x[41] / Vsp_tissue      # [85] Csp_apap
    Csp_blood_apap = x[42] / Vsp_blood      # [86] Csp_blood_apap
    Csp_blood_co2c13 = x[43] / Vsp_blood      # [87] Csp_blood_co2c13
    Csp_blood_metc13 = x[44] / Vsp_blood      # [88] Csp_blood_metc13
    Csp_co2c13 = x[45] / Vsp_tissue      # [89] Csp_co2c13
    Csp_metc13 = x[46] / Vsp_tissue      # [90] Csp_metc13
    Mar_apap = Car_apap * p[45]      # [91] Mar_apap
    Mar_co2c13 = Car_co2c13 * p[46]      # [92] Mar_co2c13
    Mar_metc13 = Car_metc13 * p[47]      # [93] Mar_metc13
    Mve_apap = Cve_apap * p[45]      # [94] Mve_apap
    Mve_co2c13 = Cve_co2c13 * p[46]      # [95] Mve_co2c13
    Mve_metc13 = Cve_metc13 * p[47]      # [96] Mve_metc13
    P_CO2c12 = (1 / (1 + p[49])) * P_CO2      # [97] P_CO2c12
    Qgu = QC * p[16]      # [98] Qgu
    Qh = QC * p[17]      # [99] Qh
    Qhe = QC * p[18]      # [100] Qhe
    Qki = QC * p[19]      # [101] Qki
    Qlu = QC * p[20]      # [102] Qlu
    Qre = QC * FQre      # [103] Qre
    Qsp = QC * p[21]      # [104] Qsp
    Vre_blood = Vre * p[34]      # [105] Vre_blood
    Vre_tissue = Vre * (1 - p[34])      # [106] Vre_tissue
    APAPD = APAPD_CLliv * 1 * (Cli_apap / (Cli_apap + p[2]))      # [107] APAPD
    CYP1A2MET = CYP1A2MET_CLliv * 1 * (Cli_metc13 / (Cli_metc13 + p[13]))      # [108] CYP1A2MET
    Cre_apap = x[35] / Vre_tissue      # [109] Cre_apap
    Cre_blood_apap = x[36] / Vre_blood      # [110] Cre_blood_apap
    Cre_blood_co2c13 = x[37] / Vre_blood      # [111] Cre_blood_co2c13
    Cre_blood_metc13 = x[38] / Vre_blood      # [112] Cre_blood_metc13
    Cre_co2c13 = x[39] / Vre_tissue      # [113] Cre_co2c13
    Cre_metc13 = x[40] / Vre_tissue      # [114] Cre_metc13
    Exhalation_co2c13 = p[14] * 60 * Clu_co2c13 / (p[15] + Clu_co2c13)      # [115] Exhalation_co2c13
    Flow_ar_gu_apap = Qgu * Car_apap      # [116] Flow_ar_gu_apap
    Flow_ar_gu_co2c13 = Qgu * Car_co2c13      # [117] Flow_ar_gu_co2c13
    Flow_ar_gu_metc13 = Qgu * Car_metc13      # [118] Flow_ar_gu_metc13
    Flow_ar_he_apap = Qhe * Car_apap      # [119] Flow_ar_he_apap
    Flow_ar_he_co2c13 = Qhe * Car_co2c13      # [120] Flow_ar_he_co2c13
    Flow_ar_he_metc13 = Qhe * Car_metc13      # [121] Flow_ar_he_metc13
    Flow_ar_ki_apap = Qki * Car_apap      # [122] Flow_ar_ki_apap
    Flow_ar_ki_co2c13 = Qki * Car_co2c13      # [123] Flow_ar_ki_co2c13
    Flow_ar_ki_metc13 = Qki * Car_metc13      # [124] Flow_ar_ki_metc13
    Flow_ar_re_apap = Qre * Car_apap      # [125] Flow_ar_re_apap
    Flow_ar_re_co2c13 = Qre * Car_co2c13      # [126] Flow_ar_re_co2c13
    Flow_ar_re_metc13 = Qre * Car_metc13      # [127] Flow_ar_re_metc13
    Flow_ar_sp_apap = Qsp * Car_apap      # [128] Flow_ar_sp_apap
    Flow_ar_sp_co2c13 = Qsp * Car_co2c13      # [129] Flow_ar_sp_co2c13
    Flow_ar_sp_metc13 = Qsp * Car_metc13      # [130] Flow_ar_sp_metc13
    Flow_gu_li_apap = Qgu * Cgu_blood_apap * p[3]      # [131] Flow_gu_li_apap
    Flow_gu_li_co2c13 = Qgu * Cgu_blood_co2c13 * p[4]      # [132] Flow_gu_li_co2c13
    Flow_gu_li_metc13 = Qgu * Cgu_blood_metc13 * p[5]      # [133] Flow_gu_li_metc13
    Flow_he_ve_apap = Qhe * Che_blood_apap * p[3]      # [134] Flow_he_ve_apap
    Flow_he_ve_co2c13 = Qhe * Che_blood_co2c13 * p[4]      # [135] Flow_he_ve_co2c13
    Flow_he_ve_metc13 = Qhe * Che_blood_metc13 * p[5]      # [136] Flow_he_ve_metc13
    Flow_ki_ve_apap = Qki * Cki_blood_apap * p[3]      # [137] Flow_ki_ve_apap
    Flow_ki_ve_co2c13 = Qki * Cki_blood_co2c13 * p[4]      # [138] Flow_ki_ve_co2c13
    Flow_ki_ve_metc13 = Qki * Cki_blood_metc13 * p[5]      # [139] Flow_ki_ve_metc13
    Flow_li_ve_apap = Qh * Cli_blood_apap * p[3]      # [140] Flow_li_ve_apap
    Flow_li_ve_co2c13 = Qh * Cli_blood_co2c13 * p[4]      # [141] Flow_li_ve_co2c13
    Flow_li_ve_metc13 = Qh * Cli_blood_metc13 * p[5]      # [142] Flow_li_ve_metc13
    Flow_lu_ar_apap = Qlu * Clu_blood_apap * p[3]      # [143] Flow_lu_ar_apap
    Flow_lu_ar_co2c13 = Qlu * Clu_blood_co2c13 * p[4]      # [144] Flow_lu_ar_co2c13
    Flow_lu_ar_metc13 = Qlu * Clu_blood_metc13 * p[5]      # [145] Flow_lu_ar_metc13
    Flow_sp_li_apap = Qsp * Csp_blood_apap * p[3]      # [146] Flow_sp_li_apap
    Flow_sp_li_co2c13 = Qsp * Csp_blood_co2c13 * p[4]      # [147] Flow_sp_li_co2c13
    Flow_sp_li_metc13 = Qsp * Csp_blood_metc13 * p[5]      # [148] Flow_sp_li_metc13
    Flow_ve_lu_apap = Qlu * Cve_apap      # [149] Flow_ve_lu_apap
    Flow_ve_lu_co2c13 = Qlu * Cve_co2c13      # [150] Flow_ve_lu_co2c13
    Flow_ve_lu_metc13 = Qlu * Cve_metc13      # [151] Flow_ve_lu_metc13
    Qha = Qh - Qgu - Qsp      # [152] Qha
    RenalClearance_apap = p[7] * Cki_apap      # [153] RenalClearance_apap
    RenalClearance_co2c13 = p[8] * Cki_co2c13      # [154] RenalClearance_co2c13
    RenalClearance_metc13 = p[9] * Cki_metc13      # [155] RenalClearance_metc13
    import_gu_apap = p[54] * Vgu_tissue * (p[41] * Cgu_blood_apap - Cgu_apap)      # [156] import_gu_apap
    import_gu_co2c13 = p[55] * Vgu_tissue * (p[42] * Cgu_blood_co2c13 - Cgu_co2c13)      # [157] import_gu_co2c13
    import_gu_metc13 = p[56] * Vgu_tissue * (p[43] * Cgu_blood_metc13 - Cgu_metc13)      # [158] import_gu_metc13
    import_he_apap = p[54] * Vhe_tissue * (p[41] * Che_blood_apap - Che_apap)      # [159] import_he_apap
    import_he_co2c13 = p[55] * Vhe_tissue * (p[42] * Che_blood_co2c13 - Che_co2c13)      # [160] import_he_co2c13
    import_he_metc13 = p[56] * Vhe_tissue * (p[43] * Che_blood_metc13 - Che_metc13)      # [161] import_he_metc13
    import_ki_apap = p[54] * Vki_tissue * (p[41] * Cki_blood_apap - Cki_apap)      # [162] import_ki_apap
    import_ki_co2c13 = p[55] * Vki_tissue * (p[42] * Cki_blood_co2c13 - Cki_co2c13)      # [163] import_ki_co2c13
    import_ki_metc13 = p[56] * Vki_tissue * (p[43] * Cki_blood_metc13 - Cki_metc13)      # [164] import_ki_metc13
    import_li_apap = p[54] * Vli_tissue * (p[41] * Cli_blood_apap - Cli_apap)      # [165] import_li_apap
    import_li_co2c13 = p[55] * Vli_tissue * (p[42] * Cli_blood_co2c13 - Cli_co2c13)      # [166] import_li_co2c13
    import_li_metc13 = p[56] * Vli_tissue * (p[43] * Cli_blood_metc13 - Cli_metc13)      # [167] import_li_metc13
    import_lu_apap = p[54] * Vlu_tissue * (p[41] * Clu_blood_apap - Clu_apap)      # [168] import_lu_apap
    import_lu_co2c13 = p[55] * Vlu_tissue * (p[42] * Clu_blood_co2c13 - Clu_co2c13)      # [169] import_lu_co2c13
    import_lu_metc13 = p[56] * Vlu_tissue * (p[43] * Clu_blood_metc13 - Clu_metc13)      # [170] import_lu_metc13
    import_sp_apap = p[54] * Vsp_tissue * (p[41] * Csp_blood_apap - Csp_apap)      # [171] import_sp_apap
    import_sp_co2c13 = p[55] * Vsp_tissue * (p[42] * Csp_blood_co2c13 - Csp_co2c13)      # [172] import_sp_co2c13
    import_sp_metc13 = p[56] * Vsp_tissue * (p[43] * Csp_blood_metc13 - Csp_metc13)      # [173] import_sp_metc13
    Flow_ar_li_apap = Qha * Car_apap      # [174] Flow_ar_li_apap
    Flow_ar_li_co2c13 = Qha * Car_co2c13      # [175] Flow_ar_li_co2c13
    Flow_ar_li_metc13 = Qha * Car_metc13      # [176] Flow_ar_li_metc13
    Flow_re_ve_apap = Qre * Cre_blood_apap * p[3]      # [177] Flow_re_ve_apap
    Flow_re_ve_co2c13 = Qre * Cre_blood_co2c13 * p[4]      # [178] Flow_re_ve_co2c13
    Flow_re_ve_metc13 = Qre * Cre_blood_metc13 * p[5]      # [179] Flow_re_ve_metc13
    P_CO2c13 = (p[49] / (1 + p[49])) * P_CO2 + Exhalation_co2c13 / 60      # [180] P_CO2c13
    import_re_apap = p[54] * Vre_tissue * (p[41] * Cre_blood_apap - Cre_apap)      # [181] import_re_apap
    import_re_co2c13 = p[55] * Vre_tissue * (p[42] * Cre_blood_co2c13 - Cre_co2c13)      # [182] import_re_co2c13
    import_re_metc13 = p[56] * Vre_tissue * (p[43] * Cre_blood_metc13 - Cre_metc13)      # [183] import_re_metc13
    DOB = ((P_CO2c13 / P_CO2c12 - p[49]) / p[49]) * 1000      # [184] DOB
    P_CO2Fc13 = P_CO2c13 / (P_CO2c12 + P_CO2c13)      # [185] P_CO2Fc13
    P_CO2R = P_CO2c13 / P_CO2c12      # [186] P_CO2R

    # ode
    list(c(
        dAar_apap = (-Flow_ar_gu_apap - Flow_ar_he_apap - Flow_ar_ki_apap - Flow_ar_li_apap) + Flow_lu_ar_apap - Flow_ar_sp_apap - Flow_ar_re_apap,
        dAar_co2c13 = (-Flow_ar_gu_co2c13 - Flow_ar_he_co2c13 - Flow_ar_ki_co2c13 - Flow_ar_li_co2c13) + Flow_lu_ar_co2c13 - Flow_ar_sp_co2c13 - Flow_ar_re_co2c13,
        dAar_metc13 = (-Flow_ar_gu_metc13 - Flow_ar_he_metc13 - Flow_ar_ki_metc13 - Flow_ar_li_metc13) + Flow_lu_ar_metc13 - Flow_ar_sp_metc13 - Flow_ar_re_metc13,
        dAbreath_co2c13 = Exhalation_co2c13,
        dAgu_apap = import_gu_apap,
        dAgu_blood_apap = (Absorption_apap - import_gu_apap) + Flow_ar_gu_apap - Flow_gu_li_apap,
        dAgu_blood_co2c13 = (Absorption_co2c13 - import_gu_co2c13) + Flow_ar_gu_co2c13 - Flow_gu_li_co2c13,
        dAgu_blood_metc13 = (Absorption_metc13 - import_gu_metc13) + Flow_ar_gu_metc13 - Flow_gu_li_metc13,
        dAgu_co2c13 = import_gu_co2c13,
        dAgu_metc13 = import_gu_metc13,
        dAhe_apap = import_he_apap,
        dAhe_blood_apap = -import_he_apap + Flow_ar_he_apap - Flow_he_ve_apap,
        dAhe_blood_co2c13 = -import_he_co2c13 + Flow_ar_he_co2c13 - Flow_he_ve_co2c13,
        dAhe_blood_metc13 = -import_he_metc13 + Flow_ar_he_metc13 - Flow_he_ve_metc13,
        dAhe_co2c13 = import_he_co2c13,
        dAhe_metc13 = import_he_metc13,
        dAki_apap = -RenalClearance_apap + import_ki_apap,
        dAki_blood_apap = -import_ki_apap + Flow_ar_ki_apap - Flow_ki_ve_apap,
        dAki_blood_co2c13 = -import_ki_co2c13 + Flow_ar_ki_co2c13 - Flow_ki_ve_co2c13,
        dAki_blood_metc13 = -import_ki_metc13 + Flow_ar_ki_metc13 - Flow_ki_ve_metc13,
        dAki_co2c13 = -RenalClearance_co2c13 + import_ki_co2c13,
        dAki_metc13 = -RenalClearance_metc13 + import_ki_metc13,
        dAli_apap = (CYP1A2MET - APAPD) + import_li_apap,
        dAli_blood_apap = ((Flow_gu_li_apap - import_li_apap) + Flow_ar_li_apap - Flow_li_ve_apap) + Flow_sp_li_apap,
        dAli_blood_co2c13 = ((Flow_gu_li_co2c13 - import_li_co2c13) + Flow_ar_li_co2c13 - Flow_li_ve_co2c13) + Flow_sp_li_co2c13,
        dAli_blood_metc13 = ((Flow_gu_li_metc13 - import_li_metc13) + Flow_ar_li_metc13 - Flow_li_ve_metc13) + Flow_sp_li_metc13,
        dAli_co2c13 = CYP1A2MET + import_li_co2c13,
        dAli_metc13 = -CYP1A2MET + import_li_metc13,
        dAlu_apap = import_lu_apap,
        dAlu_blood_apap = -import_lu_apap + Flow_ve_lu_apap - Flow_lu_ar_apap,
        dAlu_blood_co2c13 = -import_lu_co2c13 + Flow_ve_lu_co2c13 - Flow_lu_ar_co2c13,
        dAlu_blood_metc13 = -import_lu_metc13 + Flow_ve_lu_metc13 - Flow_lu_ar_metc13,
        dAlu_co2c13 = -Exhalation_co2c13 + import_lu_co2c13,
        dAlu_metc13 = import_lu_metc13,
        dAre_apap = import_re_apap,
        dAre_blood_apap = -import_re_apap + Flow_ar_re_apap - Flow_re_ve_apap,
        dAre_blood_co2c13 = -import_re_co2c13 + Flow_ar_re_co2c13 - Flow_re_ve_co2c13,
        dAre_blood_metc13 = -import_re_metc13 + Flow_ar_re_metc13 - Flow_re_ve_metc13,
        dAre_co2c13 = import_re_co2c13,
        dAre_metc13 = import_re_metc13,
        dAsp_apap = import_sp_apap,
        dAsp_blood_apap = -import_sp_apap + Flow_ar_sp_apap - Flow_sp_li_apap,
        dAsp_blood_co2c13 = -import_sp_co2c13 + Flow_ar_sp_co2c13 - Flow_sp_li_co2c13,
        dAsp_blood_metc13 = -import_sp_metc13 + Flow_ar_sp_metc13 - Flow_sp_li_metc13,
        dAsp_co2c13 = import_sp_co2c13,
        dAsp_metc13 = import_sp_metc13,
        dAurine_apap = RenalClearance_apap,
        dAurine_co2c13 = RenalClearance_co2c13,
        dAurine_metc13 = RenalClearance_metc13,
        dAve_apap = (Injection_apap + Infusion_apap + Flow_he_ve_apap + Flow_ki_ve_apap + Flow_li_ve_apap - Flow_ve_lu_apap) + Flow_re_ve_apap,
        dAve_co2c13 = (Injection_co2c13 + Infusion_co2c13 + Flow_he_ve_co2c13 + Flow_ki_ve_co2c13 + Flow_li_ve_co2c13 - Flow_ve_lu_co2c13) + Flow_re_ve_co2c13,
        dAve_metc13 = (Injection_metc13 + Infusion_metc13 + Flow_he_ve_metc13 + Flow_ki_ve_metc13 + Flow_li_ve_metc13 - Flow_ve_lu_metc13) + Flow_re_ve_metc13,
        dIVDOSE_apap = -Injection_apap * p[45],
        dIVDOSE_co2c13 = -Injection_co2c13 * p[46],
        dIVDOSE_metc13 = -Injection_metc13 * p[47],
        dPODOSE_apap = -Absorption_apap * p[45],
        dPODOSE_co2c13 = -Absorption_co2c13 * p[46],
        dPODOSE_metc13 = -Absorption_metc13 * p[47],
        dcum_dose_apap = p[50] * 60,
        dcum_dose_co2c13 = p[51] * 60,
        dcum_dose_metc13 = p[52] * 60
    ))
}

f_y <- function(times, x, p){
    # Calculate y

    Absorption_apap = (p[38] * x[56] / p[45]) * p[31]  # [1] Absorption_apap
    Absorption_co2c13 = (p[39] * x[57] / p[46]) * p[32]  # [2] Absorption_co2c13
    Absorption_metc13 = (p[40] * x[58] / p[47]) * p[33]  # [3] Absorption_metc13
    BSA = 0.024265 * (p[6] / 1)**0.5378 * (p[35] / 1)**0.3964  # [4] BSA
    CO = p[6] * p[10] + (p[36] - p[37]) * p[11] / 60  # [5] CO
    FQre = 1 - (p[18] + p[16] + p[19] + p[17] + p[21])  # [6] FQre
    FVre = 1 - (p[24] + p[23] + p[25] + p[26] + p[27] + p[28] + p[29] + p[22])  # [7] FVre
    Infusion_apap = (p[50] / p[45]) * 60  # [8] Infusion_apap
    Infusion_co2c13 = (p[51] / p[46]) * 60  # [9] Infusion_co2c13
    Infusion_metc13 = (p[52] / p[47]) * 60  # [10] Infusion_metc13
    Ki_apap = (1.386 / p[58]) * 3600  # [11] Ki_apap
    Ki_co2c13 = (1.386 / p[59]) * 3600  # [12] Ki_co2c13
    Ki_metc13 = (1.386 / p[60]) * 3600  # [13] Ki_metc13
    Var = p[6] * p[22] - (p[22] / (p[22] + p[29])) * p[6] * p[34] * (1 - p[29] - p[22])  # [14] Var
    Vgu = p[6] * p[23]  # [15] Vgu
    Vhe = p[6] * p[24]  # [16] Vhe
    Vki = p[6] * p[25]  # [17] Vki
    Vli = p[6] * p[26]  # [18] Vli
    Vlu = p[6] * p[27]  # [19] Vlu
    Vsp = p[6] * p[28]  # [20] Vsp
    Vve = p[6] * p[29] - (p[29] / (p[22] + p[29])) * p[6] * p[34] * (1 - p[29] - p[22])  # [21] Vve
    Xbreath_co2c13 = x[4] * p[46]  # [22] Xbreath_co2c13
    Xurine_apap = x[47] * p[45]  # [23] Xurine_apap
    Xurine_co2c13 = x[48] * p[46]  # [24] Xurine_co2c13
    Xurine_metc13 = x[49] * p[47]  # [25] Xurine_metc13
    APAPD_CLliv = p[1] * p[44] * Vli * p[30] * 60 / 1000  # [26] APAPD_CLliv
    CYP1A2MET_CLliv = p[12] * p[44] * Vli * p[30] * 60 / 1000  # [27] CYP1A2MET_CLliv
    Car_apap = x[1] / Var  # [28] Car_apap
    Car_co2c13 = x[2] / Var  # [29] Car_co2c13
    Car_metc13 = x[3] / Var  # [30] Car_metc13
    Cve_apap = x[50] / Vve  # [31] Cve_apap
    Cve_co2c13 = x[51] / Vve  # [32] Cve_co2c13
    Cve_metc13 = x[52] / Vve  # [33] Cve_metc13
    Injection_apap = Ki_apap * x[53] / p[45]  # [34] Injection_apap
    Injection_co2c13 = Ki_co2c13 * x[54] / p[46]  # [35] Injection_co2c13
    Injection_metc13 = Ki_metc13 * x[55] / p[47]  # [36] Injection_metc13
    P_CO2 = BSA * p[48] / 60  # [37] P_CO2
    QC = (CO / 1000) * 3600  # [38] QC
    Vgu_blood = Vgu * p[34]  # [39] Vgu_blood
    Vgu_tissue = Vgu * (1 - p[34])  # [40] Vgu_tissue
    Vhe_blood = Vhe * p[34]  # [41] Vhe_blood
    Vhe_tissue = Vhe * (1 - p[34])  # [42] Vhe_tissue
    Vki_blood = Vki * p[34]  # [43] Vki_blood
    Vki_tissue = Vki * (1 - p[34])  # [44] Vki_tissue
    Vli_blood = Vli * p[34]  # [45] Vli_blood
    Vli_tissue = Vli * (1 - p[34])  # [46] Vli_tissue
    Vlu_blood = Vlu * p[34]  # [47] Vlu_blood
    Vlu_tissue = Vlu * (1 - p[34])  # [48] Vlu_tissue
    Vre = p[6] * FVre  # [49] Vre
    Vsp_blood = Vsp * p[34]  # [50] Vsp_blood
    Vsp_tissue = Vsp * (1 - p[34])  # [51] Vsp_tissue
