"""Test USPCE query type."""
from telliot_feeds.queries.ampleforth.uspce import AmpleforthUSPCE


def test_uspce_query():
    """Validate USPCE query id and data"""
    q = AmpleforthUSPCE()

    print(q.query_data.hex())
    print(q.query_id.hex())

    assert (
        q.query_data.hex()
        == ""  # noqa: E501
    )
    assert q.query_id.hex() == "612ec1d9cee860bb87deb6370ed0ae43345c9302c085c1dfc4c207cbec2970d7"
